const characters = [
  { 
    name: "Goku",
    img: "https://vignette.wikia.nocookie.net/dbz-dokkanbattle/images/2/21/Dl5OdcoU0AAtMfQ.jpeg/revision/latest/scale-to-width-down/340?cb=20181122082552",
    show: "DBZ"
  },
  { 
    name: "Midoriya", 
    img: "https://comicvine1.cbsistatic.com/uploads/scale_small/11117/111173561/5994041-8086170340-63780.jpg", 
    show: "My Hero" 
  },
  { name: "Yoruichi", img: "https://vignette.wikia.nocookie.net/bleachhd/images/3/3b/Yoruichi_42.jpg/revision/latest?cb=20140811132400", show: "Bleach" },
  { name: "Pikachu", img: "https://images-na.ssl-images-amazon.com/images/I/61iWqqcq%2BKL._AC_SL1500_.jpg", show: "Pokemon" },
  { name: "Killua", img: "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/aca3db94-66f5-4419-8b62-6cf248cbc2c0/ddwldra-a450ea5e-e937-422e-acda-5518084e83a4.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvYWNhM2RiOTQtNjZmNS00NDE5LThiNjItNmNmMjQ4Y2JjMmMwXC9kZHdsZHJhLWE0NTBlYTVlLWU5MzctNDIyZS1hY2RhLTU1MTgwODRlODNhNC5wbmcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.zwYqdNnTOxPwWvChMwVm6KylF8DPC_98dUS0S52NrCQ", show: "Hunter x Hunter" },
  { name: "Sakura", img: "https://i.pinimg.com/originals/21/d2/cb/21d2cb3fdfbeb8ce40a6fa20c732f4c2.jpg", show: "Naruto" },
  { name: "Appa", img: "https://i.pinimg.com/originals/5b/ac/dd/5bacddbf9477d583ac425aa336196fba.jpg", show: "Avatar" }
]

export const getAll = () => characters;

export const getByShow = show => characters.filter(char => char.show === show);

