import React from "react";

export default class NewCharacterForm extends React.Component {
  constructor() {
    super();

    this.state = {
      name: "",
      img: "", 
      show: ""
    };
  }

  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    if (this.state.name === '' || this.state.show === '') return
    this.props.handleLogin(this.state)
  }

  render() {
    return (
      <form className="ui form" onSubmit={this.handleSubmit}>
        <div className="field">
          <label>
            Name
            <input id="name" name="name" type="text"
              onChange={ this.handleInputChange }
              value={this.state.name}
              />
          </label>
        </div>
        <div className="field">
          <label>
            Name
            <input id="name" name="name" type="text"
              onChange={ this.handleInputChange }
              value={this.state.name}
              />
          </label>
        </div>
        <div className="field">
          <label>
            Show
            <input id="show" name="show" type="show" 
              onChange={ this.handleInputChange }
              value={this.state.show}/>
          </label>
        </div>
        <div className="field"> 
          <label>
            Image
            <input id="img" name="img" type="img" 
              onChange={ this.handleInputChange }
              value={this.state.img}/>
          </label>
        </div>

        <div classname="field">
          <button className="ui secondary button" type="submit">Log in</button>
        </div>
      </form>
    );
  }
}
